import React from "react";
import "./BSlider.css";
import HeroSlider, { Slide } from "hero-slider";
import img1 from "./Sliderimg/img1.jpg";
import img2 from "./Sliderimg/img2.jpg";
import img3 from "./Sliderimg/img3.jpeg";
import img4 from "./Sliderimg/img4.jpg";
import { Container, Row, Col } from "react-bootstrap";
const pic1 = img1;
const pic2 = img2;
const pic3 = img3;
const pic4 = img4;

const BSlider = () => {
  return (
    <div>
      <Row>
        <Col lg={12} className="bslide-col-1">
          <HeroSlider
            slidingAnimation="left_to_right"
            orientation="horizontal"
            initialSlide={1}
            onBeforeChange={(previousSlide, nextSlide) =>
              console.log("onBeforeChange", previousSlide, nextSlide)
            }
            onChange={(nextSlide) => console.log("onChange", nextSlide)}
            onAfterChange={(nextSlide) =>
              console.log("onAfterChange", nextSlide)
            }
            //style={{ backgroundColor: "red" }}
            settings={{
              slidingDuration: 250,
              slidingDelay: 100,
              shouldAutoplay: true,
              shouldDisplayButtons: true,
              autoplayDuration: 5000,
              height: "100vh",
            }}
          >
            <Slide
              background={{
                backgroundImage: pic1,
                backgroundAttachment: "fixed",
              }}
            />
            <Slide
              background={{
                backgroundImage: pic2,
                backgroundAttachment: "fixed",
              }}
            />
            <Slide
              background={{
                backgroundImage: pic3,
                backgroundAttachment: "fixed",
              }}
            />
            <Slide
              background={{
                backgroundImage: pic4,
                backgroundAttachment: "fixed",
              }}
            />
          </HeroSlider>
        </Col>
      </Row>
    </div>
  );
};
export default BSlider;
