import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import img1 from "./CompanyOverviewimg/innovation.jpeg";
import "./CompanyOverview.css";
function CompanyOverview() {
  return (
    <div>
      <Container>
        <Row>
          <div className="col-md-4">
            <img src={img1} alt="ino" className="img1" />
          </div>
          <div className="col-md-5">
            <h1 className="heading1">Automation Competence</h1>
            <p>
              Our business is to help your business achieve manufacturing
              excellence. We work continuously to bring you the latest thinking
              in automation technology, enabling you to operate more quickly,
              more effectively and more smartly than our competitors Products &
              solutions you can rely on, delivery when you want it and answers
              when you need them. As a manufacturer of automation control panels
              and channel partner to leading brands in automation products, we
              understand the essence of quality, that underpins everything that
              we do: in product identification and manufacturing, in systems and
              support, in delivery and after - sales service.
            </p>
          </div>
        </Row>
      </Container>
    </div>
  );
}
export default CompanyOverview;
