import { ServiceDropDown } from "./Navitems";
import { TrainingDropDown } from "./Navitems";
import { Link } from "react-router-dom";
import "./Dropdown.css";
import { useState } from "react";
function Dropdown() {
  const [servicedropdown, setservicedropdown] = useState(false);
  const [trainingdropdown, settrainingdropdown] = useState(false);
  return (
    <>
      <div>
        <ul
          className={
            servicedropdown ? "services-submenu clicked" : "services-submenu"
          }
          onClick={() => setservicedropdown(!servicedropdown)}
        >
          {ServiceDropDown.map((item) => {
            return (
              <li key={item.id}>
                <Link
                  to={item.path}
                  className={item.cName}
                  onClick={() => {
                    setservicedropdown(false);
                  }}
                >
                  {item.title}
                </Link>
              </li>
            );
          })}
        </ul>
      </div>
      <div>
        {" "}
        <ul
          className={
            trainingdropdown ? "training-submenu clicked" : "training-submenu"
          }
          onClick={() => settrainingdropdown(!trainingdropdown)}
        >
          {TrainingDropDown.map((item) => {
            return (
              <li key={item.id}>
                <Link
                  to={item.path}
                  className={item.cName}
                  onClick={() => {
                    settrainingdropdown(false);
                  }}
                >
                  {item.title}
                </Link>
              </li>
            );
          })}
        </ul>
      </div>
    </>
  );
}
export default Dropdown;
