    (settings.slidingDuration + settings.slidingDelay) * 1.1
  ); // 110% safety factor.

  const setSlidingAnimation = React.useCallback(
    (newAnimation: EAnimations) => {
      setSettings({
        ...settings,
        slidingAnimation: newAnimation,
      });
    },
    [settings],
  );

