  );

  const isLoaded = className === 'slide-background-mask-loaded';

  return (
    <Mask
      className={[
        'slide-background-mask',
        (props.isActive && props.isDoneSliding) ? 'slide-background-mask-active' : 'slide-background-mask-inactive',
