import React from "react";
import { useState, useEffect } from "react";
import image from "./Data";
import "./BSlider.css";
import HeroSlider, { Slide } from "hero-slider";
//import img1 from "./Sliderimg/img1.jpg";
//import img2 from "./Sliderimg/img2.jpg";
//import img3 from "./Sliderimg/img3.jpeg";
//import img4 from "./Sliderimg/img4.jpg";
import { Container, Row, Col } from "react-bootstrap";

const BSlider = () => {
  const [currentState, setcurrentState] = useState(0);
  useEffect(() => {
    const timer = setTimeout(() => {
      if (currentState === 3) {
        setcurrentState(0);
      } else {
        setcurrentState(currentState + 1);
      }
    }, 5000);
    return () => clearTimeout(timer);
  }, [currentState]);
  return (
    <div>
      <Row>
        <Col lg={12} className="bslide-col-1">
          <HeroSlider
          //slidingAnimation="left_to_right"
          //orientation="horizontal"
          //initialSlide={1}
          /* onBeforeChange={(previousSlide, nextSlide) =>
              console.log("onBeforeChange", previousSlide, nextSlide)
            }
            onChange={(nextSlide) => console.log("onChange", nextSlide)}
            onAfterChange={(nextSlide) =>
              console.log("onAfterChange", nextSlide)
            }*/

          /* settings={{
              slidingDuration: 250,
              slidingDelay: 100,
              shouldAutoplay: true,
              shouldDisplayButtons: true,
              autoplayDuration: 5000,
              height: "100vh",
            }}*/
          >
            <Slide
              background={{
                backgroundImage: `url(${image[currentState].url})`,
                backgroundAttachment: "fixed",
                backgroundRepeat: "no-repeat",
                backgroundSize: "cover",
                backgroundPosition: "center",
              }}
            />
            <Slide
              background={{
                backgroundImage: `url(${image[currentState].url})`,
                backgroundAttachment: "fixed",
                backgroundRepeat: "no-repeat",
                backgroundSize: "cover",
                backgroundPosition: "center",
              }}
            />
            <Slide
              background={{
                backgroundImage: `url(${image[currentState].url})`,
                backgroundAttachment: "fixed",
                backgroundRepeat: "no-repeat",
                backgroundSize: "cover",
                backgroundPosition: "center",
              }}
            />
            <Slide
              background={{
                backgroundImage: `url(${image[currentState].url})`,
                backgroundAttachment: "fixed",
                backgroundRepeat: "no-repeat",
                backgroundSize: "cover",
                backgroundPosition: "center",
              }}
            ></Slide>
          </HeroSlider>
        </Col>
      </Row>
    </div>
  );
};
export default BSlider;
