import React, { useState } from "react";
//import BackgroundSlider from "./BackgroundSlider";
import BSlider from "./BSlider";
import img1 from "./Homeimg/center-logo.png";
import "./Home.css";
import { Container, Row, Col } from "react-bootstrap";
import img2 from "./Homeimg/automation_projects.jpg";

import img3 from "./Homeimg/training.jpg";
import img4 from "./Homeimg/industrial_application_software.jpg";
import img5 from "./Homeimg/industrial_panels.jpg";
import img6 from "./Homeimg/amc.jpg";

function Home() {
  const [mousenotin, setmousenotin] = useState(img2);
  const handleMouseEnter = () => {
    setmousenotin(
      <div>
        <img src={img2} alt="" className="home-img-2" />
        <h5>Automation</h5>
      </div>
    );
  };
  const handleMouseLeave = () => {
    setmousenotin(img2);
  };

  return (
    <div>
      <BSlider />
      <Container>
        <Row className="home-row-1">
          <Col lg={12} className="home-col-1">
            <img src={img1} alt="" className="home-img-1" />
          </Col>
        </Row>
      </Container>
      <Container>
        <Row className="home-row-2">
          <Col lg={12} className="home-col-2">
            <h1 className="home-head-1">
              <span className="home-span-1">
                Emerging Industrial Automation
              </span>
              <br /> <span className="home-span-2">Solution</span>
              <br /> <span className="home-span-3">Providing Across Asia</span>
            </h1>
          </Col>
        </Row>
      </Container>
      <Container>
        <Row className="home-row-3">
          <Col lg={12} className="home-col-3">
            <h1 className="home-head-2">
              What We<span className="home-span-4"> Do</span>
            </h1>
          </Col>
        </Row>
      </Container>
      <Container>
        <Row className="home-row-4">
          <Col lg={3}>
            <div
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
            >
              <img src={mousenotin} />
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
}
export default Home;
